//Task组件
import React, { Component } from 'react';  ////默认导入React

import { Tasks } from '../api/tasks.js';  //导入Tasks集合


// Task component - 它有对应每个输入对象的todo自己
export default class Task extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textEditing: false,
    };
  }

  toggleTextEditing(){
    this.setState({
      textEditing:!this.state.textEditing,
    })
  }

  toggleChecked() {
     // 当触发时就把checked状态调整为当前状态的相反值
     Tasks.update(this.props.task._id, {  //.update 采取两个参数，参数一是集合子集的选择器，参数二是对匹配对象的更新参数
       $set: { checked: !this.props.task.checked },
     });
  }

  deleteThisTask() {  //.remove() 采取一个参数，这个参数是一个选择器，决定哪个目标被删除
     Tasks.remove(this.props.task._id);
  }

  liClicked() {  //.remove() 采取一个参数，这个参数是一个选择器，决定哪个目标被删除
     Tasks.update(this.props.task._id,{
       $set:{ onClick: !this.props.task.onClick },
     });
  }

  render() {
  	//三元表达式，对每个<li>设置taskClassName,从未对应不同的CSS外表
    const taskClassName = this.props.task.checked ? 'checked' : '';
    const PrivateOrPublic = this.props.task.checked ? 'public' :'private';
    const EditOrSave = !this.state.textEditing ? 'EditText' : 'SaveChange'

    return (
      <li className={taskClassName}>
         <button className="delete" onClick={this.deleteThisTask.bind(this)}>
           &times;
         </button>
         <input
           id="inputTrueFalse"
           type="checkbox"
           readOnly
           checked={!!this.props.task.checked}
           onClick={this.toggleChecked.bind(this)}
         />
         <button className="editText" onClick={this.toggleTextEditing.bind(this)}> 
           {EditOrSave}
         </button>

      <div className="toggleTrueFalse" onClick={this.liClicked.bind(this)}>
           <span className="text">
              Created By {this.props.task.username}: 
              <br/> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              {!this.state.textEditing ? <span id="toggleColor">Title:{this.props.task.textTitle}</span> :
              <span id="toggleTextChange">Title:<input id="titleChange" 
                 type="text"
                 ref="titleChange"
                 placeholder={this.props.task.textTitle}/></span> }
           </span>
           <br /> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
           <span className="text_status">
              Status:{PrivateOrPublic}
           </span>
           { this.props.task.onClick ?
           '' : 
            <div id="areaText">
            <span className="text">
              {this.props.task.textArea}
            </span>
            </div>
           }
      </div>
       </li>
    );
  }
}
