//App总组件
import React, { Component } from 'react';  //默认导入React
import ReactDOM from 'react-dom';  //为了使用event-handler
import { Meteor } from 'meteor/meteor';
import { withTracker } from 'meteor/react-meteor-data';  //把我门的App组件转化为更高阶的组件

import { Tasks } from '../api/tasks.js'; //导入Tasks集合

import Task from './Task.js';  //导入子集合
import AccountsUIWrapper from './AccountsUIWrapper.js';  //导入账号密码包

// App component - 代表整个App的组件集合
class App extends Component {
  //输入表格的event-handler
  handleSubmit(event) {
     event.preventDefault();
     // 通过React 的 ref 找到 textInput 的值
     const textTitle = ReactDOM.findDOMNode(this.refs.titleInput).value.trim();
     const textArea = ReactDOM.findDOMNode(this.refs.areaInput).value.trim();
     Tasks.insert({
       textTitle,
       textArea,
       createdAt: new Date(), //当前时间的数据
       owner: Meteor.userId(),           // 已登录用户的 _id
       username: Meteor.user().username,  // 已登录用户的username
     });
     // 清空表格
     ReactDOM.findDOMNode(this.refs.titleInput).value = '';
     ReactDOM.findDOMNode(this.refs.areaInput).value = '';
  }

  renderTasks() {
    let filteredTasks = this.props.tasks;
    const currentUserId = this.props.currentUser && this.props.currentUser._id;
    const wrongUser = task.owner != currentUserId;
    if (!this.props.currentUser && wrongUser) {
      filteredTasks = filteredTasks.filter(task => task.checked);
    }
      return filteredTasks.map((task) => {
      const showPrivateButton = task.owner === currentUserId;

      return (
        <Task
          key={task._id}
          task={task}
          showPrivateButton={showPrivateButton}
        />
      );
    });
  }

  render() {
    return (
      <div className="container">
        <header>
          <h1>The Diary Book</h1>
          {/*未完成的任务数目在此显示*/} 
          { this.props.currentUser ? <h3>PrivateDiary:({this.props.privateCount})</h3> : ''}

          <AccountsUIWrapper />

          {/*添加一个输入表格,并且使用三元表达式，如果已经有用户登录，则显示输入框<form>，没有用户登录则不显示*/}
          { this.props.currentUser ?
             <form className="new-task" onSubmit={this.handleSubmit.bind(this)}>
               <input
                 id="title"
                 type="text"
                 ref="titleInput"
                 placeholder="Type The Diary Title"
               />
               <textarea className="new-text-area" 
                 type="text"
                 ref="areaInput"
                 placeholder="Say Something Today..."
               />
               <br />
               <button className="form-submit" type="submit">Submit</button>
             </form> : ''
          }
        </header>

        <ul>
          {this.renderTasks()}
        </ul>
      </div>
    );
  }
}
//把tasks设定为App的props，tasks里的数据是从Tasks里fetch到的
export default withTracker(() => {
  Meteor.subscribe('tasks');
   return {
     tasks: Tasks.find({}, { sort: { createdAt: -1 } }).fetch(),  //把数据根据插入顺序排序然后再抓取出来,并且设置成props
     privateCount: Tasks.find({ checked: { $ne: true } }).count(),  //数一下有多少未完成,并且设置成props
     currentUser: Meteor.user(), //获得当前已登录用户的信息，并且设置成props
   };
})(App);
