//App总组件
import React, { Component } from 'react';  //默认导入React
import ReactDOM from 'react-dom';  //为了使用event-handler
import { withTracker } from 'meteor/react-meteor-data';  //把我门的App组件转化为更高阶的组件

import { Tasks } from '../api/tasks.js'; //导入Tasks集合

import Task from './Task.js';  //导入子集合

// App component - 代表整个App的组件集合
class App extends Component {
  //为筛选表格设置初始 hideCompleted state
  constructor(props) {
    super(props);
    this.state = {
      hideCompleted: false,
    };
  }

  //输入表格的event-handler
  handleSubmit(event) {
     event.preventDefault();
     // 通过React 的 ref 找到 textInput 的值
     const text = ReactDOM.findDOMNode(this.refs.textInput).value.trim();
     Tasks.insert({
       text,
       createdAt: new Date(), //当前时间的数据
     });
     // 清空表格
     ReactDOM.findDOMNode(this.refs.textInput).value = '';
  }

  //hideCompleted 的 event-handler
  toggleHideCompleted() {
    this.setState({
      hideCompleted: !this.state.hideCompleted,
    });
  }

  renderTasks() {
    let filteredTasks = this.props.tasks;  //声明仅在本作用域内有效的变量
    if (this.state.hideCompleted) {
       filteredTasks = filteredTasks.filter(tasksItem => !tasksItem.checked);
     }
    return filteredTasks.map((tasksItem) => (
      <Task key={tasksItem._id} task={tasksItem} />  //Task子组件在这里被传入key/task两个props
    ));
  }

  render() {
    return (
      <div className="container">
        <header>
          <h1>Zion's Todo List</h1>
          <h3>IncompleteCount:({this.props.incompleteCount})</h3>{/*未完成的任务数目在此显示*/}
          {/*添加一个查看 hideCompleted 的筛选表格*/}
          <label className="hide-completed">
             <input
               type="checkbox"

               checked={this.state.hideCompleted}
               onClick={this.toggleHideCompleted.bind(this)}
             />
             Hide Completed Tasks
          </label>

          {/*添加一个输入表格*/}
          <form className="new-task" onSubmit={this.handleSubmit.bind(this)} >
             <input
               type="text"
               ref="textInput"
               placeholder="Type to add new tasks"
             />
          </form>
        </header>

        <ul>
          {this.renderTasks()}
        </ul>
      </div>
    );
  }
}
//把tasks设定为App的props，tasks里的数据是从Tasks里fetch到的
export default withTracker(() => {
   return {
     tasks: Tasks.find({}, { sort: { createdAt: -1 } }).fetch(),  //把数据根据插入顺序排序然后再抓取出来,并且设置成props
     incompleteCount: Tasks.find({ checked: { $ne: true } }).count(),  //数一下有多少未完成,并且设置成props
   };
})(App);
